import React, { useState } from "react";
import axios from "axios";
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import "./App.css";

const App = () => {
  const [video, setVideo] = useState(null);
  const [videoPreview, setVideoPreview] = useState(null);
  const [processedVideo, setProcessedVideo] = useState(null);
  const [loading, setLoading] = useState(false);
  const [progress, setProgress] = useState(0);
  const [isDragging, setIsDragging] = useState(false);

  const handleDragOver = (e) => {
    e.preventDefault();
    setIsDragging(true);
  };

  const handleDragLeave = () => {
    setIsDragging(false);
  };

  const handleDrop = (e) => {
    e.preventDefault();
    setIsDragging(false);

    const file = e.dataTransfer.files[0];
    handleFileChange(file);
  };

  const handleFileChange = (file) => {
    if (file && file.type.startsWith("video/")) {
      setVideo(file);
      setVideoPreview(URL.createObjectURL(file));
      toast.success("Video selected!");
    } else {
      toast.error("Please select a valid video file!");
    }
  };

  const handleUpload = async () => {
    if (!video) {
      toast.error("Please upload a video!");
      return;
    }

    const formData = new FormData();
    formData.append("file", video);

    setLoading(true);
    setProgress(0);

    try {
      const response = await axios.post(
        "http://127.0.0.1:8000/api/videos/upload/",
        formData,
        {
          headers: { "Content-Type": "multipart/form-data" },
        }
      );

      toast.success("Video uploaded successfully! Processing started.");
      const videoId = response.data.id;

      // Start polling for processing status
      pollProcessingStatus(videoId);
    } catch (error) {
      toast.error("Error uploading video!");
      console.error(error.response?.data || error.message);
      setLoading(false);
    }
  };

  const pollProcessingStatus = async (videoId) => {
    try {
      const response = await axios.get(
        `http://127.0.0.1:8000/api/videos/status/${videoId}/`
      );

      const { progress: backendProgress, video_url, status } = response.data;
      setProgress(backendProgress);

      if (status === "completed") {
        toast.success("Processing completed!");
        setProcessedVideo(video_url);
        setLoading(false);
      } else if (status === "failed") {
        toast.error("Processing failed!");
        setLoading(false);
      } else {
        setTimeout(() => pollProcessingStatus(videoId), 3000);
      }
    } catch (error) {
      toast.error("Error fetching processing status.");
      console.error(error.message);
      setLoading(false);
    }
  };

  const handleDownload = () => {
    if (processedVideo) {
      const downloadUrl = `http://127.0.0.1:8000${processedVideo.replace(
        "/media",
        ""
      )}`;
      const link = document.createElement("a");
      link.href = downloadUrl;
      link.download = "processed_video.mp4";
      document.body.appendChild(link);
      link.click();
      document.body.removeChild(link);
    }
  };

  return (
    <div className="app-container">
      <h1>Google-Style Drag-and-Drop Video Upload</h1>

      <div
        className={`dropzone ${isDragging ? "dragging" : ""}`}
        onDragOver={handleDragOver}
        onDragLeave={handleDragLeave}
        onDrop={handleDrop}
      >
        {videoPreview ? (
          <video src={videoPreview} controls style={{ width: "100%" }} />
        ) : (
          <div>
            <p>Drag & Drop your video here, or click to select</p>
          </div>
        )}
        <input
          type="file"
          onChange={(e) => handleFileChange(e.target.files[0])}
          accept="video/*"
          style={{ display: "none" }}
        />
      </div>

      <button
        className="upload-btn"
        onClick={handleUpload}
        disabled={loading || !video}
      >
        {loading ? "Processing..." : "Upload and Process"}
      </button>

      {loading && (
        <div className="progress-bar">
          <p>Progress: {Math.round(progress)}%</p>
          <div style={{ width: "100%", background: "#ddd", height: "20px" }}>
            <div
              style={{
                width: `${progress}%`,
                background: "green",
                height: "100%",
              }}
            ></div>
          </div>
        </div>
      )}

      {processedVideo && (
        <div className="video-container">
          <h2>Processed Video</h2>
          <video
            src={`http://127.0.0.1:8000${processedVideo.replace("/media", "")}`}
            controls
            style={{ width: "100%" }}
          />
          <button onClick={handleDownload} className="download-btn">
            Download Processed Video
          </button>
        </div>
      )}

      <ToastContainer />
    </div>
  );
};

export default App;
