import React, { useState } from 'react';
import axios from 'axios';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

const App = () => {
  const [video, setVideo] = useState(null);
  const [processedVideo, setProcessedVideo] = useState(null);
  const [loading, setLoading] = useState(false);

  const handleFileChange = (e) => {
    setVideo(e.target.files[0]);
  };

  const handleUpload = async () => {
    if (!video) {
      toast.error('Please upload a video!');
      return;
    }

    const formData = new FormData();
    formData.append('file', video);

    setLoading(true);
    try {
      // Send the video to the backend for processing
      const response = await axios.post('http://127.0.0.1:8000/api/videos/upload/', formData, {
        headers: { 'Content-Type': 'multipart/form-data' },
      });

      // Show success message and the processed video URL
      toast.success('Video uploaded and processed successfully!');
      setProcessedVideo(response.data.video_url);
    } catch (error) {
      toast.error('Error uploading video!');
      console.error(error.response?.data || error.message);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div style={{ padding: '20px' }}>
      <h1>Video Editor</h1>

      {/* File input for video */}
      <input type="file" onChange={handleFileChange} accept="video/mp4,video/x-m4v,video/*" />

      {/* Upload button */}
      <button onClick={handleUpload} disabled={loading} style={{ marginTop: '10px' }}>
        {loading ? 'Processing...' : 'Upload and Process'}
      </button>

      {/* Display processed video */}
      {processedVideo && (
        <div style={{ marginTop: '20px' }}>
          <h3>Processed Video:</h3>
          <video controls style={{ maxWidth: '100%' }}>
            <source src={`http://127.0.0.1:8000${processedVideo}`} type="video/mp4" />
            Your browser does not support the video tag.
          </video>
        </div>
      )}

      {/* Toast container for notifications */}
      <ToastContainer />
    </div>
  );
};

export default App;
