import React, { useState, useRef, useEffect } from "react";
import axios from "axios";
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import "./App.css";

const App = () => {
  const [video, setVideo] = useState(null);
  const [overlay, setOverlay] = useState(null);
  const [processedVideo, setProcessedVideo] = useState(null);
  const [loading, setLoading] = useState(false);
  const [progress, setProgress] = useState(0);
  const [cancelTokenSource, setCancelTokenSource] = useState(null);
  const [startTime, setStartTime] = useState("");
  const [endTime, setEndTime] = useState("");
  const [currentStage, setCurrentStage] = useState("");
  const videoRef = useRef(null);
  const beforeVideoRef = useRef(null);

  const handleFileChange = (e) => {
    setVideo(e.target.files[0]);
  };

  const handleOverlayChange = (e) => {
    setOverlay(e.target.files[0]);
  };

  const validateTime = (time) => {
    const regex = /^([0-9]{2}):([0-9]{2}):([0-9]{2})$/; // Format: HH:MM:SS
    return regex.test(time);
  };

  const handleUpload = async () => {
    if (!video) {
      toast.error("Please upload a video!");
      return;
    }

    if (startTime && !validateTime(startTime)) {
      toast.error("Invalid start time format. Use HH:MM:SS.");
      return;
    }

    if (endTime && !validateTime(endTime)) {
      toast.error("Invalid end time format. Use HH:MM:SS.");
      return;
    }

    const formData = new FormData();
    formData.append("file", video);
    formData.append("start_time", startTime);
    formData.append("end_time", endTime);
    if (overlay) {
      formData.append("overlay", overlay);
    }

    setLoading(true);
    setProgress(0);

    const source = axios.CancelToken.source();
    setCancelTokenSource(source);

    try {
      const response = await axios.post(
        "http://127.0.0.1:8000/api/videos/upload/",
        formData,
        {
          headers: { "Content-Type": "multipart/form-data" },
          cancelToken: source.token,
        }
      );

      toast.success("Video uploaded successfully! Processing started.");
      const videoId = response.data.id;

      // Start polling
      pollProcessingStatus(videoId);
    } catch (error) {
      if (axios.isCancel(error)) {
        toast.info("Upload canceled.");
      } else {
        toast.error("Error uploading video!");
        console.error(error.response?.data || error.message);
      }
      setLoading(false);
    }
  };

  const pollProcessingStatus = async (videoId) => {
    try {
      const response = await axios.get(
        `http://127.0.0.1:8000/api/videos/status/${videoId}/`
      );
      const { progress, video_url, status, current_stage } = response.data;

      // Set the current stage of processing
      setCurrentStage(current_stage);

      // Calculate the new progress based on the stage
      let newProgress = progress;

      // Example of manually adjusting progress based on stages
      if (current_stage === "Detecting Speech") {
        newProgress = Math.min(progress + 10, 100); // Increase progress by 10% for each stage
      } else if (current_stage === "Trimming Video") {
        newProgress = Math.min(progress + 15, 100);
      } else if (current_stage === "Applying Overlay") {
        newProgress = Math.min(progress + 25, 100);
      }

      setProgress(newProgress);

      // If the video is processed
      if (status === "completed") {
        toast.success("Processing completed!");
        setProcessedVideo(video_url);
        setLoading(false);
      } else if (status === "failed") {
        toast.error("Processing failed!");
        setLoading(false);
      } else {
        // Continue polling
        toast.info(`Current Stage: ${current_stage}`);
        setTimeout(() => pollProcessingStatus(videoId), 3000);
      }
    } catch (error) {
      toast.error("Error fetching processing status.");
      setLoading(false);
    }
  };

  const handleCancel = () => {
    if (cancelTokenSource) {
      cancelTokenSource.cancel("Upload canceled by the user.");
      setLoading(false);
    }
  };

  return (
    <div className="app-container">
      <h1>Advanced Video Editor</h1>
      <div className="upload-section">
        <input type="file" onChange={handleFileChange} accept="video/*" />
        <input
          type="file"
          onChange={handleOverlayChange}
          accept="image/*"
          placeholder="Overlay Image"
        />
        <div className="time-inputs">
          <label>
            Start Time:
            <input
              type="text"
              value={startTime}
              onChange={(e) => setStartTime(e.target.value)}
              placeholder="00:00:00"
            />
          </label>
          <label>
            End Time:
            <input
              type="text"
              value={endTime}
              onChange={(e) => setEndTime(e.target.value)}
              placeholder="00:00:00"
            />
          </label>
        </div>
        <button onClick={handleUpload} disabled={loading}>
          {loading ? "Processing..." : "Upload and Process"}
        </button>
        {loading && <button onClick={handleCancel}>Cancel</button>}
      </div>
      {loading && (
        <div className="progress-bar">
          <p>Progress: {progress}%</p>
          <div style={{ width: "100%", background: "#ddd", height: "20px" }}>
            <div
              style={{
                width: `${progress}%`,
                background: "green",
                height: "100%",
              }}
            ></div>
          </div>
          <p>Processing Stage: {currentStage}</p>
        </div>
      )}
      <div className="video-container">
        {video && (
          <div className="video-section">
            <h2>Original Video</h2>
            <video
              ref={beforeVideoRef}
              src={URL.createObjectURL(video)}
              controls
              style={{ width: "100%", maxWidth: "600px" }}
            />
          </div>
        )}
        {processedVideo && (
          <div className="video-section">
            <h2>Processed Video</h2>
            <video
              src={`http://127.0.0.1:8000${processedVideo.replace('/media', '')}`}
              controls
              style={{ width: "100%", maxWidth: "600px" }}
            />
          </div>
        )}
      </div>
      <ToastContainer />
    </div>
  );
};

export default App;
