import React, { useState, useRef } from "react";
import axios from "axios";
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import "./App.css";

const App = () => {
  const [video, setVideo] = useState(null);
  const [processedVideo, setProcessedVideo] = useState(null);
  const [loading, setLoading] = useState(false);
  const [progress, setProgress] = useState(0);
  const [cancelTokenSource, setCancelTokenSource] = useState(null);
  const [startTime, setStartTime] = useState("");
  const [endTime, setEndTime] = useState("");
  const [transcription, setTranscription] = useState("");
  const [overlay, setOverlay] = useState(null);
  const videoRef = useRef(null);

  const handleFileChange = (e) => {
    setVideo(e.target.files[0]);
  };

  const handleOverlayChange = (e) => {
    setOverlay(e.target.files[0]);
  };

  const handleUpload = async () => {
    if (!video) {
      toast.error("Please upload a video!");
      return;
    }

    const formData = new FormData();
    formData.append("file", video);
    formData.append("start_time", startTime);
    formData.append("end_time", endTime);
    if (overlay) {
      formData.append("overlay", overlay);
    }

    setLoading(true);
    setProgress(0);

    const source = axios.CancelToken.source();
    setCancelTokenSource(source);

    try {
      const response = await axios.post("http://127.0.0.1:8000/api/videos/upload/", formData, {
        headers: { "Content-Type": "multipart/form-data" },
        cancelToken: source.token,
      });

      toast.success("Video uploaded successfully! Processing started.");
      const videoId = response.data.id;

      // Start polling
      pollProcessingStatus(videoId);
    } catch (error) {
      if (axios.isCancel(error)) {
        toast.info("Upload canceled.");
      } else {
        toast.error("Error uploading video!");
        console.error(error.response?.data || error.message);
      }
      setLoading(false);
    }
  };

  const pollProcessingStatus = async (videoId) => {
    try {
      const response = await axios.get(`http://127.0.0.1:8000/api/videos/status/${videoId}/`);
      const { progress, video_url, status, transcription } = response.data;

      setProgress(progress);

      if (status === "completed") {
        toast.success("Processing completed!");
        setProcessedVideo(video_url);
        setTranscription(transcription);
        setLoading(false);
      } else if (status === "failed") {
        toast.error("Processing failed!");
        setLoading(false);
      } else {
        setTimeout(() => pollProcessingStatus(videoId), 3000);
      }
    } catch (error) {
      toast.error("Error fetching processing status.");
      setLoading(false);
    }
  };

  const handleCancel = () => {
    if (cancelTokenSource) {
      cancelTokenSource.cancel("Upload canceled by the user.");
      setLoading(false);
    }
  };

  return (
    <div>
      <h1>Advanced Video Editor</h1>
      <input type="file" onChange={handleFileChange} accept="video/*" />
      <input type="file" onChange={handleOverlayChange} accept="image/*" placeholder="Overlay Image" />
      <div>
        <label>
          Start Time:
          <input type="text" value={startTime} onChange={(e) => setStartTime(e.target.value)} placeholder="00:00:00" />
        </label>
        <label>
          End Time:
          <input type="text" value={endTime} onChange={(e) => setEndTime(e.target.value)} placeholder="00:00:00" />
        </label>
      </div>
      <button onClick={handleUpload} disabled={loading}>
        {loading ? "Processing..." : "Upload and Process"}
      </button>
      {loading && <button onClick={handleCancel}>Cancel</button>}
      {loading && (
        <div>
          <p>Progress: {progress}%</p>
          <div style={{ width: "100%", background: "#ddd", height: "20px" }}>
            <div style={{ width: `${progress}%`, background: "green", height: "100%" }}></div>
          </div>
        </div>
      )}
      {processedVideo && (
        <div>
          <h2>Processed Video</h2>
          <video src={`http://127.0.0.1:8000${processedVideo}`} controls />
          <h3>Transcription</h3>
          <p>{transcription}</p>
        </div>
      )}
      <ToastContainer />
    </div>
  );
};

export default App;
