import React, { useState } from 'react';
import axios from 'axios';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

const App = () => {
  const [video, setVideo] = useState(null);
  const [processedVideo, setProcessedVideo] = useState(null);
  const [loading, setLoading] = useState(false);

  const handleFileChange = (e) => {
    setVideo(e.target.files[0]);
  };

  const handleUpload = async () => {
    if (!video) {
      toast.error('Please upload a video!');
      return;
    }

    const formData = new FormData();
    formData.append('file', video);

    setLoading(true);
    try {
      const response = await axios.post('http://127.0.0.1:8000/api/videos/upload/', formData, {
        headers: { 'Content-Type': 'multipart/form-data' },
      });

      toast.success('Video uploaded and processed successfully!');
      setProcessedVideo(response.data.video_url);
    } catch (error) {
      toast.error('Error uploading video!');
      console.error(error.response?.data || error.message);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div>
      <h1>Video Editor</h1>
      <input type="file" onChange={handleFileChange} />
      <button onClick={handleUpload} disabled={loading}>
        {loading ? 'Processing...' : 'Upload and Process'}
      </button>

      {processedVideo && (
        <div>
          <h3>Processed Video:</h3>
          <video controls>
            <source src={`http://127.0.0.1:8000${processedVideo}`} type="video/mp4" />
          </video>
        </div>
      )}
      <ToastContainer />
    </div>
  );
};

export default App;
